import './App.css';

function App() {
  return (
    <div className = "application-frame" class = "h-screen w-screen flex flex-col items-center justify-center py-8" >
      <div className = "application" class = "flex flex-col h-full w-11/12 text-left divide-y divide-solid">
        <div className = "header-obj" class = "flex flex-col h-fit pb-3">
          <p className = "header-text" class = "text-2xl font-bold">A Tool For Understanding and Visualizing N-Grams.</p>
          <p className = "subheader-text">An interactive tool to learn and visualize how n-gram models - one of the most powerful tools for statistical natural language processing - operate.</p>
        </div>
        <div className = "text-processing" class = "flex flex-col space-y-5 h-2/6 items-center justify-center" >
          <p className = "text-entrance-text">Enter a sample passage below and choose an n-gram model to get started:</p>
          <textarea className = "gram-model-text" type = "textarea" class = "rounded-md p-2 h-5/6 w-8/12 outline outline-black-50 focus:outline-none focus:ring focus:border-black-50 "></textarea>
        </div>
      </div>
    </div>
  );
}

export default App;
