import React, {useState, useEffect} from "react";
import ReactDOM from 'react-dom';
import axios from "axios";
axios.defaults.baseURL = "http://localhost:3001"

//Function
export default function GeneratePassage(){
    return (
        <div className = "generated-passage-section" class = "flex flex-col space-y-5 h-3/6 w-full align-center text-center items-center justify-center">
            <div className = "generated-text" class = "font-bold">Now, click the following button to generate a passage based on the n-gram.</div>
            <button className = "build-ngram-dict" class = "bg-red-500 text-white font-bold rounded-md w-2/12 h-10 outline outline-1 hover:bg-slate-700 hover:ring">Generate Text</button>
            <div className = "generated-text" class = "w-10/12 h-5/6 outline outline-red-50 rounded-md overflow-y"></div>
        </div>
    )
}
