import './App.css';

function App() {
  return (
    <div className = "application-frame" class = "h-screen w-screen flex flex-col items-center justify-center bg-slate-500 py-8" >
      <div className = "application" class = "flex flex-col h-full w-11/12 text-left bg-slate-200">
        <p className = "header-text" class = "text-2xl font-bold">A Tool For Understanding and Visualizing N-Gram tools.</p>
        <p className = "subheader-text">An interactive tool to learn and visualize how n-gram models - one of the most powerful tools for statistical natural language processing - operate.</p>
      </div>
    </div>
  );
}

export default App;
